import React, { useEffect, useState } from "react";
import axios from "axios";

const EditProfile = ({ user, onUpdate }) => {
  const [editedUser, setEditedUser] = useState({ ...user });

  const handleChange = (e) => {
    setEditedUser((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(
        `http://localhost:3000/user/${editedUser.user_id}`,
        editedUser
      );
      onUpdate(); // Trigger parent component to reload user data
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h3>Edit Profile</h3>
      <form onSubmit={handleSubmit}>
        {/* Display existing user data */}
        <div>
          <strong>User ID:</strong> {user.user_id}
        </div>
        <label htmlFor="First_Name">First Name:</label>
        <input
          type="text"
          id="edit-firstName"
          name="First_Name"
          value={editedUser.First_Name}
          onChange={handleChange}
          required
        />

        <label htmlFor="Middle_Name">Middle Name:</label>
        <input
          type="text"
          id="edit-middleName"
          name="Middle_Name"
          value={editedUser.Middle_Name}
          onChange={handleChange}
        />

        <label htmlFor="Last_Name">Last Name:</label>
        <input
          type="text"
          id="edit-lastName"
          name="Last_Name"
          value={editedUser.Last_Name}
          onChange={handleChange}
          required
        />

        {/* Add other fields as needed */}

        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

const UserDashboard = () => {
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    // Fetch user data when the component mounts
    const fetchUserData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/user"); // Replace with your endpoint
        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, [editMode]);

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleUpdate = () => {
    setEditMode(false);
  };

  return (
    <div>
      <h2>User Dashboard</h2>
      {user ? (
        <div>
          {/* Display user information */}
          <div>
            <strong>User ID:</strong> {user.user_id}
          </div>
          <div>
            <strong>First Name:</strong> {user.First_Name}
          </div>
          <div>
            <strong>Middle Name:</strong> {user.Middle_Name}
          </div>
          <div>
            <strong>Last Name:</strong> {user.Last_Name}
          </div>
          {/* Add other fields as needed */}

          {!editMode && (
            <button type="button" onClick={handleEditClick}>
              Edit Profile
            </button>
          )}

          {editMode && <EditProfile user={user} onUpdate={handleUpdate} />}
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
};

export default UserDashboard;
