import React, { useState, useEffect } from "react";
import "./DonationAppt.css";

const DonationAppt = () => {
  const [selectedUser, setSelectedUser] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [appointments, setAppointments] = useState([]);
  const [editingAppointmentId, setEditingAppointmentId] = useState(null);

  const handleUserChange = (event) => {
    setSelectedUser(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
  };

  const scheduleAppointment = () => {
    if (
      selectedUser.trim() !== "" &&
      selectedDate.trim() !== "" &&
      selectedTime.trim() !== ""
    ) {
      const currentDate = new Date();
      const selectedAppointmentDate = new Date(selectedDate);

      if (selectedAppointmentDate <= currentDate) {
        alert("Please select a future date for the appointment.");
        return;
      }

      const appointmentData = {
        USER_ID: selectedUser,
        Status: "Scheduled",
        Appointment_Date: selectedDate,
        Appointment_Time: selectedTime,
      };

      fetch("http://localhost:3000/schedule-appointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(appointmentData),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setAppointments((prevAppointments) => [
            ...prevAppointments,
            {
              USER_ID: selectedUser,
              Status: "Scheduled",
              Appointment_Date: selectedDate,
              Appointment_Time: selectedTime,
            },
          ]);

          setSelectedUser("");
          setSelectedDate("");
          setSelectedTime("");

          alert("Appointment scheduled successfully.");
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
          alert("Error scheduling appointment. Please try again later.");
        });
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await fetch("http://localhost:3000/get-appointments");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setAppointments(data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const handleEditAppointment = (appointmentId) => {
    const appointmentToEdit = appointments.find(
      (appointment) => appointment.APPOINTMENT_ID === appointmentId
    );

    setSelectedUser(appointmentToEdit.USER_ID);
    setSelectedDate(appointmentToEdit.Appointment_Date);
    setSelectedTime(appointmentToEdit.Appointment_Time);
    setEditingAppointmentId(appointmentId);
  };

  const handleSaveEdit = () => {
    if (editingAppointmentId) {
      const updatedAppointment = {
        APPOINTMENT_ID: editingAppointmentId,
        USER_ID: selectedUser,
        Appointment_Date: selectedDate,
        Appointment_Time: selectedTime,
      };

      fetch("http://localhost:3000/edit-appointment", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedAppointment),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
              appointment.APPOINTMENT_ID === editingAppointmentId
                ? updatedAppointment
                : appointment
            )
          );

          setSelectedUser("");
          setSelectedDate("");
          setSelectedTime("");
          setEditingAppointmentId(null);

          alert("Appointment updated successfully.");
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
          alert("Error updating appointment. Please try again later.");
        });
    }
  };

  return (
    <div className="donation-appt-container">
      <header>
        <h1>Donation Appointment</h1>
      </header>
      <div className="user-selection-container">
        <p>Select a user for the donation appointment:</p>
        <input
          type="text"
          value={selectedUser}
          onChange={handleUserChange}
          className="user-selector"
        />
      </div>
      <div className="date-selection-container">
        <p>Select a date for your donation appointment:</p>
        <input
          type="date"
          value={selectedDate}
          onChange={handleDateChange}
          className="date-picker"
        />
      </div>
      <div className="time-selection-container">
        <p>Select a time for your donation appointment:</p>
        <input
          type="time"
          value={selectedTime}
          onChange={handleTimeChange}
          className="time-picker"
        />
      </div>
      <button
        disabled={
          selectedUser.trim() === "" ||
          selectedDate.trim() === "" ||
          selectedTime.trim() === ""
        }
        onClick={editingAppointmentId ? handleSaveEdit : scheduleAppointment}
      >
        {editingAppointmentId ? "Save Edit" : "Schedule Appointment"}
      </button>
      <div className="appointments-container">
        <h2>Your Appointments:</h2>
        <table>
          <thead>
            <tr>
              <th>User ID</th>
              <th>Status</th>
              <th>Appointment Date</th>
              <th>Appointment Time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appointment) => (
              <tr key={appointment.APPOINTMENT_ID}>
                <td>{appointment.USER_ID}</td>
                <td>{appointment.Status}</td>
                <td>{appointment.Appointment_Date.split("T")[0]}</td>
                <td>{appointment.Appointment_Time}</td>
                <td>
                  <button
                    onClick={() =>
                      handleEditAppointment(appointment.APPOINTMENT_ID)
                    }
                  >
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DonationAppt;
