// Admin_Dashboard.js

import React, { useState, useEffect } from "react";
import { Routes, Route, useParams, Navigate } from "react-router-dom";
import axios from "axios";
import AdminSidebar from "./AdminSidebar.js";
import "./Admin_Dashboard.css";
import UserDashboard from "./User_Dashboard.js";

const Admin_Dashboard = () => {
  return (
    <div className="admin-dashboard">
      <AdminSidebar />
      <div className="admin-content">
        <Routes>
          <Route path="/" element={<DashboardHome />} />
          <Route path="user-dashboard" element={<UserDashboard />} />
          <Route path="users/:action" element={<UsersSection />} />
          {/* Add more routes as needed */}
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = () => {
  return <h2>Welcome to the Admin Dashboard!</h2>;
};

const UsersSection = () => {
  let { action } = useParams();

  // Render different components based on the route parameter
  if (action === "edit") {
    return <EditUser />;
  } else if (action === "create") {
    return <CreateUser />;
  } else {
    // Render other components or default view
    return <h2>User Section</h2>;
  }
};

const EditUser = () => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [newUserInfo, setNewUserInfo] = useState({
    User_ID: "",
    First_Name: "",
    Middle_Name: "",
    Last_Name: "",
    Username: "",
    Password: "",
    Email: "",
    Phone_No: "",
    Blood_Group: "",
    Last_Donation_Date: "",
  });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch the user data when the component mounts
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userId = ""; // Add the actual user ID or fetch it from props/location
        const res = await axios.get(`http://localhost:3000/user/${userId}`);
        setSelectedUser(res.data);

        // Populate all the empty fields with the user's information
        setNewUserInfo({
          ...res.data,
          First_Name: res.data.First_Name || "",
          Middle_Name: res.data.Middle_Name || "",
          Last_Name: res.data.Last_Name || "",
          Username: res.data.Username || "",
          Password: res.data.Password || "",
          Email: res.data.Email || "",
          Phone_No: res.data.Phone_No || "",
          Blood_Group: res.data.Blood_Group || "",
          Last_Donation_Date: res.data.Last_Donation_Date || "",
        });
      } catch (err) {
        console.error("Error fetching user:", err);
      }
    };

    fetchUser();
  }, []);

  const handleChangeUserInfo = async () => {
    // Filter out User_ID from the newUserInfo
    const { User_ID, ...updatedInfo } = newUserInfo;

    try {
      // Only send request if there are changes
      if (JSON.stringify(updatedInfo) !== "{}") {
        await axios.put(`http://localhost:3000/user/${User_ID}`, newUserInfo);
        console.log("User info updated successfully!");

        // Refetch the user after successful update
        const res = await axios.get(`http://localhost:3000/user/${User_ID}`);
        setSelectedUser(res.data);
      } else {
        console.log("No changes to update.");
      }
    } catch (err) {
      console.error("Error updating user info:", err);
    }

    setIsEditing(false); // Reset editing mode
  };

  const handleDeleteUser = async () => {
    try {
      if (selectedUser && selectedUser.User_ID) {
        await axios.delete(`http://localhost:3000/user/${selectedUser.User_ID}`);
        console.log("User deleted successfully!");

        // Redirect or navigate to user dashboard after successful deletion
        // Implement the necessary navigation logic here

        setSelectedUser(null); // Reset selectedUser
        setNewUserInfo({
          User_ID: "",
          First_Name: "",
          Middle_Name: "",
          Last_Name: "",
          Username: "",
          Password: "",
          Email: "",
          Phone_No: "",
          Blood_Group: "",
          Last_Donation_Date: "",
        });
        setIsEditing(false); // Reset editing mode
      } else {
        console.log("No user selected to delete.");
      }
    } catch (err) {
      console.error("Error deleting user:", err);
    }
  };

  return (
    <div>
      <h2>Edit User Information</h2>
      {selectedUser && (
        <div>
          {/* Display user details or form for editing */}
          {isEditing ? (
            // Render a form for editing the user info
            <div>
              {/* Input fields for editing */}
              <button onClick={handleChangeUserInfo}>Save Changes</button>
              <button onClick={handleDeleteUser}>Delete User</button>
            </div>
          ) : (
            // Render a button to enable editing mode
            <button onClick={() => setIsEditing(true)}>Edit User Info</button>
          )}
        </div>
      )}
    </div>
  );
};

const CreateUser = () => {
  return <h2>Create User</h2>;
};

export default Admin_Dashboard;
