// AdminSidebar.js

import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const AdminSidebar = () => {
  const location = useLocation();
  const [activeLink, setActiveLink] = useState(location.pathname);

  const handleLinkClick = (path) => {
    setActiveLink(path);
  };

  return (
    <div className="admin-sidebar">
      <Link to="/admin-dashboard" className={activeLink === '/admin-dashboard' ? 'active' : ''} onClick={() => handleLinkClick('/admin-dashboard')}>
        Dashboard
      </Link>
      <Link to="/admin-dashboard/users" className={activeLink === '/admin-dashboard/users' ? 'active' : ''} onClick={() => handleLinkClick('/admin-dashboard/users')}>
        Users
      </Link>
      <Link to="/admin-dashboard/settings" className={activeLink === '/admin-dashboard/settings' ? 'active' : ''} onClick={() => handleLinkClick('/admin-dashboard/settings')}>
        Settings
      </Link>
      {/* Add more links as needed */}
    </div>
  );
};

export default AdminSidebar;
