import React, { useState } from 'react';
import './dashboard.css'; // Assuming you have a CSS file for styling

const Inventory = () => {
  const [sendAlerts, setSendAlerts] = useState({
    'A+': false,
    'A-': false,
    'B+': false,
    'B-': false,
    'AB+': false,
    'AB-': false,
    'O+': false,
    'O-': false,
  });

  const handleSendAlert = (bloodType) => {
    setSendAlerts((prevSendAlerts) => ({
      ...prevSendAlerts,
      [bloodType]: true,
    }));
  };

  return (
    <div>
      <header>
        <h1>Inventory Dashboard</h1>
      </header>

      <nav>
        <a href="doctorDashboard.html">Home</a>
        <a href="inventory.html">Inventory</a>
        <a href="#">Logout</a>
      </nav>

      <section>
        <h2>Inventory</h2>

        <table>
          <thead>
            <tr>
              <th>Blood Type</th>
              <th>Quantity (units)</th>
              <th>Status</th>
              <th>Send Alert</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>A+</td>
              <td>50</td>
              <td>Healthy</td>
              <td>
                <button disabled={sendAlerts['A+']} onClick={() => handleSendAlert('A+')}>
                  Send Alert
                </button>
              </td>
            </tr>
            {/* Repeat similar rows for other blood types */}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default Inventory;
