import React, { useState, useEffect } from "react";
import "./DonationAppt.css";

const DonationAppt = () => {
  const [selectedUser, setSelectedUser] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [appointments, setAppointments] = useState([]);
  const [editedAppointment, setEditedAppointment] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const handleUserChange = (event) => {
    setSelectedUser(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
  };

  const scheduleAppointment = () => {
    if (
      selectedUser.trim() !== "" &&
      selectedDate.trim() !== "" &&
      selectedTime.trim() !== ""
    ) {
      const currentDate = new Date();
      const selectedAppointmentDate = new Date(selectedDate);

      // Check if the selected date is in the future
      if (selectedAppointmentDate <= currentDate) {
        alert("Please select a future date for the appointment.");
        return;
      }

      // Continue with scheduling if the date is in the future
      const appointmentData = {
        USER_ID: selectedUser,
        Status: "Scheduled",
        Appointment_Date: selectedDate,
        Appointment_Time: selectedTime,
      };

      fetch("http://localhost:3000/schedule-appointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(appointmentData),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setAppointments((prevAppointments) => [
            ...prevAppointments,
            {
              USER_ID: selectedUser,
              Status: "Scheduled",
              Appointment_Date: selectedDate,
              Appointment_Time: selectedTime,
            },
          ]);

          setSelectedUser("");
          setSelectedDate("");
          setSelectedTime("");

          alert("Appointment scheduled successfully.");
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
          alert("Error scheduling appointment. Please try again later.");
        });
    }
  };

  const handleEditAppointment = (appointment) => {
    setEditedAppointment(appointment);
    setIsEditing(true);
    setSelectedUser(appointment.USER_ID);
    setSelectedDate(appointment.Appointment_Date);
    setSelectedTime(appointment.Appointment_Time);
  };

  const handleSaveChanges = () => {
    if (
      selectedUser.trim() !== "" &&
      selectedDate.trim() !== "" &&
      selectedTime.trim() !== ""
    ) {
      const currentDate = new Date();
      const selectedAppointmentDate = new Date(selectedDate);

      // Check if the selected date is in the future
      if (selectedAppointmentDate <= currentDate) {
        alert("Please select a future date for the appointment.");
        return;
      }

      const updatedAppointment = {
        USER_ID: selectedUser,
        Status: "Scheduled",
        Appointment_Date: selectedDate,
        Appointment_Time: selectedTime,
      };

      fetch(
        `http://localhost:3000/edit-appointment/${editedAppointment.USER_ID}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedAppointment),
        }
      )
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          // Update the appointments array with the edited appointment
          setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
              appointment.USER_ID === editedAppointment.USER_ID
                ? updatedAppointment
                : appointment
            )
          );

          // Reset state
          setEditedAppointment(null);
          setIsEditing(false);
          setSelectedUser("");
          setSelectedDate("");
          setSelectedTime("");

          alert("Appointment updated successfully.");
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
          alert("Error updating appointment. Please try again later.");
        });
    }
  };

  useEffect(() => {
    // Fetch appointment data when the component mounts
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await fetch("http://localhost:3000/get-appointments");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setAppointments(data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  return (
    <div className="donation-appt-container">
      <header>
        <h1>{isEditing ? "Edit Appointment" : "Donation Appointment"}</h1>
      </header>
      <div className="user-selection-container">
        <p>Select a user for the donation appointment:</p>
        <input
          type="text"
          value={selectedUser}
          onChange={handleUserChange}
          className="user-selector"
        />
      </div>
      <div className="date-selection-container">
        <p>Select a date for your donation appointment:</p>
        <input
          type="date"
          value={selectedDate}
          onChange={handleDateChange}
          className="date-picker"
        />
      </div>
      <div className="time-selection-container">
        <p>Select a time for your donation appointment:</p>
        <input
          type="time"
          value={selectedTime}
          onChange={handleTimeChange}
          className="time-picker"
        />
      </div>
      <button
        disabled={
          selectedUser.trim() === "" ||
          selectedDate.trim() === "" ||
          selectedTime.trim() === ""
        }
        onClick={isEditing ? handleSaveChanges : scheduleAppointment}
      >
        {isEditing ? "Save Changes" : "Schedule Appointment"}
      </button>
      <div className="appointments-container">
        <h2>Your Appointments:</h2>
        <table>
          <thead>
            <tr>
              <th>User ID</th>
              <th>Status</th>
              <th>Appointment Date</th>
              <th>Appointment Time</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appointment) => (
              <tr key={appointment.USER_ID}>
                <td>{appointment.USER_ID}</td>
                <td>{appointment.Status}</td>
                <td>{appointment.Appointment_Date.split("T")[0]}</td>
                <td>{appointment.Appointment_Time}</td>
                <td>
                  <button onClick={() => handleEditAppointment(appointment)}>
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DonationAppt;
