import React, { useState } from 'react';
import './Feedback.css';

const Feedback = () => {
  const [rating, setRating] = useState(0);
  const [feedbackText, setFeedbackText] = useState('');

  const handleRatingChange = (event) => {
    setRating(Number(event.target.value));
  };

  const submitFeedback = () => {
    // Prepare the feedback data
    const feedbackData = {
      rating: rating,
      feedbackText: feedbackText,
    };

    // Assuming your backend server is running on http://localhost:3000
    const apiUrl = 'http://localhost:3000/submitFeedback';

    // Make a POST request to the server
    fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(feedbackData),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response from the server if needed
        console.log('Server response:', data);
        alert('Your feedback has been recorded.');
      })
      .catch((error) => {
        // Handle any errors that occurred during the fetch
        console.error('Error submitting feedback:', error);
        alert('Error submitting feedback. Please try again later.');
      });
  };

  return (
    <div className="feedback-container">
      <header>
        <h1>Feedback</h1>
      </header>
      <div id="rating-container">
        <p>Rate us out of 5:</p>
        <select
          value={rating}
          onChange={handleRatingChange}
          className="rating-dropdown"
        >
          {[0, 1, 2, 3, 4, 5].map((value) => (
            <option key={value} value={value}>
              {value}
            </option>
          ))}
        </select>
      </div>
      <textarea
        id="feedback-text"
        placeholder="Please enter your feedback."
        value={feedbackText}
        onChange={(e) => setFeedbackText(e.target.value)}
        className="textarea-size"
      ></textarea>
      <button
        // Ensures the button can only be pressed if feedback is present
        disabled={rating === 0 || feedbackText.trim() === ''}
        onClick={submitFeedback}
      >
        Submit
      </button>
    </div>
  );
};

export default Feedback;
